// src/pages/Dashboard.jsx
import React, { useState, useEffect, useRef } from 'react';
import { Line, Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, BarElement } from 'chart.js';
import axios from 'axios';
import moment from 'moment';
import GoogleCalendarSync from '../components/calendar/GoogleCalendarSync';
import CalendarContainer, { EventContext } from '../components/calendar/CalendarContainer';

// Register ChartJS components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, BarElement, Title, Tooltip, Legend);

const Dashboard = () => {
  const [events, setEvents] = useState([]);
  const [startingBalance, setStartingBalance] = useState(0);
  const [stats, setStats] = useState({
    income: 0,
    expenses: 0,
    total: 0
  });
  
  // Quick add form state
  const [quickAddForm, setQuickAddForm] = useState({
    title: '',
    date: moment().format('YYYY-MM-DD'),
    income: '',
    expenses: '',
    venue: '',
    category: '',
    notes: ''
  });
  
  // Form ref to reset it
  const formRef = useRef(null);
  
  // Fetch events data from API
  useEffect(() => {
    // Fetch events from the API
    const fetchEvents = async () => {
      try {
        const response = await axios.get('/api/events');
        
        // Transform the events for the calendar
        const formattedEvents = response.data.map(event => ({
          id: event.event_id || event.id,
          title: event.summary || event.title || 'Untitled Event',
          start: new Date(event.start_time || event.start),
          end: new Date(event.end_time || event.end),
          allDay: event.all_day || event.allDay || false,
          income: event.income || 0,
          expenses: event.expenses || 0,
          venue: event.location || event.venue || '',
          description: event.description || ''
        }));
        
        // Calculate stats
        let totalIncome = 0;
        let totalExpenses = 0;
        
        formattedEvents.forEach(event => {
          totalIncome += Number(event.income) || 0;
          totalExpenses += Number(event.expenses) || 0;
        });
        
        setEvents(formattedEvents);
        setStats({
          income: totalIncome,
          expenses: totalExpenses,
          total: totalIncome - totalExpenses
        });
      } catch (error) {
        console.error('Error fetching events:', error);
        
        // Use mock data as fallback
        const mockEvents = [
          {
            id: 1,
            title: 'Wedding at Grand Ballroom',
            start: new Date(2025, 2, 15),
            end: new Date(2025, 2, 16),
            income: 3000,
            expenses: 800,
            venue: 'Grand Ballroom'
          },
          {
            id: 2,
            title: 'Corporate Event',
            start: new Date(2025, 2, 20),
            end: new Date(2025, 2, 20),
            income: 2500,
            expenses: 600,
            venue: 'City Conference Center'
          }
        ];
        
        setEvents(mockEvents);
        
        setStats({
          income: 5500,
          expenses: 1400,
          total: 4100
        });
      }
    };
    
    fetchEvents();
  }, []);
  
  // Update stats when events change
  useEffect(() => {
    // Calculate stats from events
    let totalIncome = 0;
    let totalExpenses = 0;
    
    events.forEach(event => {
      totalIncome += Number(event.income) || 0;
      totalExpenses += Number(event.expenses) || 0;
    });
    
    setStats({
      income: totalIncome,
      expenses: totalExpenses,
      total: totalIncome - totalExpenses
    });
  }, [events]);
  
  // Revenue trend chart data
  const revenueData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
    datasets: [
      {
        label: 'Income',
        data: [500, 700, 800, 600, 900, 750],
        backgroundColor: 'rgba(75, 192, 192, 0.5)'
      },
      {
        label: 'Expenses',
        data: [300, 400, 500, 450, 650, 500],
        backgroundColor: 'rgba(255, 99, 132, 0.5)'
      }
    ]
  };
  
  // Income projection chart data
  const projectionData = {
    labels: ['Today', '7 Days', '14 Days', '21 Days', '30 Days'],
    datasets: [{
      label: 'Projected Income',
      data: [500, 1000, 1800, 2500, 3000],
      backgroundColor: 'rgba(75, 192, 192, 0.2)',
      borderColor: 'rgba(75, 192, 192, 1)',
      fill: true,
      tension: 0.3
    }]
  };
  
  // Handle calendar slot selection
  const handleCalendarSelect = ({ start, end }) => {
    // Update the quick add form with the selected date
    setQuickAddForm(prev => ({
      ...prev,
      date: moment(start).format('YYYY-MM-DD')
    }));
    
    // Scroll to the quick add form
    const quickAddSection = document.getElementById('quick-add-section');
    if (quickAddSection) {
      quickAddSection.scrollIntoView({ behavior: 'smooth' });
    }
  };
  
  // Handle form field changes
  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setQuickAddForm(prev => ({
      ...prev,
      [name]: value
    }));
  };
  
  // Handle adding a new event
  const handleAddEvent = async (e) => {
    e.preventDefault();
    
    // Create new event object
    const newEvent = {
      title: quickAddForm.title,
      start: new Date(quickAddForm.date),
      end: new Date(quickAddForm.date),
      income: parseFloat(quickAddForm.income || 0),
      expenses: parseFloat(quickAddForm.expenses || 0),
      venue: quickAddForm.venue,
      category: quickAddForm.category,
      notes: quickAddForm.notes
    };
    
    try {
      // Format data for API
      const eventData = {
        summary: newEvent.title,
        description: newEvent.notes,
        location: newEvent.venue,
        start_time: newEvent.start.toISOString(),
        end_time: newEvent.end.toISOString(),
        all_day: false,
        calendar_id: 1, // Default calendar ID
        // Include financial data
        income: newEvent.income,
        expenses: newEvent.expenses,
        category: newEvent.category
      };
      
      // Create event in API
      const response = await axios.post('/api/events', eventData);
      
      // Add ID from response
      newEvent.id = response.data.event_id || Date.now();
      
      // Update events state
      setEvents(prev => [...prev, newEvent]);
      
      // Update stats
      setStats(prev => ({
        income: prev.income + newEvent.income,
        expenses: prev.expenses + newEvent.expenses,
        total: prev.total + (newEvent.income - newEvent.expenses)
      }));
      
      // Reset form
      setQuickAddForm({
        title: '',
        date: moment().format('YYYY-MM-DD'),
        income: '',
        expenses: '',
        venue: '',
        category: '',
        notes: ''
      });
      
      if (formRef.current) {
        formRef.current.reset();
      }
      
      // Show success message
      alert('Event added successfully!');
      
    } catch (error) {
      console.error('Error saving event:', error);
      
      // Fallback: just add to state if API fails
      newEvent.id = Date.now(); // temporary ID
      setEvents(prev => [...prev, newEvent]);
      
      // Update stats
      setStats(prev => ({
        income: prev.income + newEvent.income,
        expenses: prev.expenses + newEvent.expenses,
        total: prev.total + (newEvent.income - newEvent.expenses)
      }));
      
      // Reset form
      setQuickAddForm({
        title: '',
        date: moment().format('YYYY-MM-DD'),
        income: '',
        expenses: '',
        venue: '',
        category: '',
        notes: ''
      });
      
      if (formRef.current) {
        formRef.current.reset();
      }
    }
  };
  
  // Handle starting balance change
  const handleBalanceChange = (e) => {
    setStartingBalance(parseFloat(e.target.value) || 0);
  };
  
  return (
    <div className="container mx-auto p-4">
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-4">
        {/* Calendar Area - Now using the CalendarContainer with selection handler */}
        <div className="lg:col-span-2 bg-gray-800 rounded-lg shadow-lg overflow-hidden">
          <div className="flex items-center justify-between bg-gray-700 p-4">
            <div className="flex items-center">
              <div className="text-2xl mr-4">☰</div>
              <h1 className="text-xl font-semibold text-white">CalCalc</h1>
            </div>
            <button
              onClick={() => document.getElementById('sync-modal').classList.toggle('hidden')}
              className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-md"
            >
              Google Calendar Sync
            </button>
            
            {/* Google Calendar Sync Modal */}
            <div id="sync-modal" className="hidden fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
              <div className="bg-gray-800 rounded-lg p-6 max-w-md w-full">
                <div className="flex justify-between items-center mb-4">
                  <h2 className="text-xl font-semibold text-white">Google Calendar Sync</h2>
                  <button
                    onClick={() => document.getElementById('sync-modal').classList.add('hidden')}
                    className="text-gray-400 hover:text-white"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                    </svg>
                  </button>
                </div>
                <GoogleCalendarSync 
                  onSyncComplete={() => {
                    // Close modal
                    document.getElementById('sync-modal').classList.add('hidden');
                    // Reload the page to refresh all data
                    window.location.reload();
                  }}
                />
              </div>
            </div>
          </div>
          <div className="p-4" style={{ height: "calc(100vh - 200px)", minHeight: "500px" }}>
            {/* Use CalendarContainer with selection handler */}
            <CalendarContainer onSelectSlot={handleCalendarSelect} />
          </div>
        </div>
        
        {/* Analytics Section */}
        <div className="bg-gray-800 rounded-lg shadow-lg p-4">
          <h2 className="text-xl font-semibold text-white mb-4">Data Analysis</h2>
          <div className="mb-4">
            <label className="block text-white mb-1">
              Starting Balance:
              <input 
                type="number" 
                value={startingBalance}
                onChange={handleBalanceChange}
                className="ml-2 p-1 rounded w-24 text-black"
                step="0.01"
              />
            </label>
          </div>
          <p className="text-white mb-2">Income: <strong>${stats.income.toFixed(2)}</strong></p>
          <p className="text-white mb-2">Expenses: <strong>${stats.expenses.toFixed(2)}</strong></p>
          <p className="text-white mb-4">Total: <strong>${stats.total.toFixed(2)}</strong></p>
          
          <div className="bg-gray-700 rounded-lg p-4 mb-4">
            <h3 className="text-lg font-semibold text-white mb-2">Revenue Trend</h3>
            <div className="h-48">
              <Bar data={revenueData} options={{ maintainAspectRatio: false }} />
            </div>
          </div>
          
          <div className="bg-gray-700 rounded-lg p-4 mb-4">
            <h3 className="text-lg font-semibold text-white mb-2">Financial Insights</h3>
            <p className="text-white">Top Earning Month: <strong>May</strong></p>
            <p className="text-white">Projected Income: <strong>$3,000</strong></p>
            <p className="text-white">Upcoming High-Paying Events: <strong>2</strong></p>
            <p className="text-white">Best Day for Earnings: <strong>Saturday</strong></p>
            <p className="text-white">Month-to-Month Growth: <strong>+12%</strong></p>
          </div>
          
          <div className="bg-gray-700 rounded-lg p-4">
            <h3 className="text-lg font-semibold text-white mb-2">Top Venues</h3>
            <p className="text-white">Grand Ballroom: <strong>$3,000</strong> (+10%)</p>
            <p className="text-white">City Conference Center: <strong>$2,500</strong> (-5%)</p>
            <p className="text-white">Lakeside Pavilion: <strong>$2,000</strong> (+7%)</p>
          </div>
        </div>
      </div>
      
      {/* Bottom Row */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-4 mt-4">
        {/* Quick Add */}
        <div id="quick-add-section" className="bg-gray-800 rounded-lg shadow-lg p-4">
          <h2 className="text-xl font-semibold text-white mb-4">Quick Add Event</h2>
          <form ref={formRef} onSubmit={handleAddEvent}>
            <div className="mb-3">
              <input 
                type="text" 
                name="title"
                placeholder="Event Title" 
                className="w-full p-2 rounded text-black"
                value={quickAddForm.title}
                onChange={handleFormChange}
                required
              />
            </div>
            <div className="mb-3">
              <input 
                type="date" 
                name="date"
                className="w-full p-2 rounded text-black"
                value={quickAddForm.date}
                onChange={handleFormChange}
                required
              />
            </div>
            <div className="mb-3">
              <input 
                type="number" 
                name="income"
                placeholder="Income" 
                step="0.01" 
                className="w-full p-2 rounded text-black"
                value={quickAddForm.income}
                onChange={handleFormChange}
              />
            </div>
            <div className="mb-3">
              <input 
                type="number" 
                name="expenses"
                placeholder="Expenses" 
                step="0.01" 
                className="w-full p-2 rounded text-black"
                value={quickAddForm.expenses}
                onChange={handleFormChange}
              />
            </div>
            <div className="mb-3">
              <select 
                name="venue"
                className="w-full p-2 rounded text-black"
                value={quickAddForm.venue}
                onChange={handleFormChange}
                required
              >
                <option value="" disabled>Select Venue</option>
                <option value="Grand Ballroom">Grand Ballroom</option>
                <option value="City Conference Center">City Conference Center</option>
                <option value="Lakeside Pavilion">Lakeside Pavilion</option>
              </select>
            </div>
            <div className="mb-3">
              <select 
                name="category"
                className="w-full p-2 rounded text-black"
                value={quickAddForm.category}
                onChange={handleFormChange}
                required
              >
                <option value="" disabled>Select Category</option>
                <option value="Wedding">Wedding</option>
                <option value="Club">Club</option>
                <option value="Corporate">Corporate</option>
                <option value="Private Party">Private Party</option>
              </select>
            </div>
            <div className="mb-3">
              <textarea 
                name="notes"
                placeholder="Notes" 
                rows="3" 
                className="w-full p-2 rounded text-black"
                value={quickAddForm.notes}
                onChange={handleFormChange}
              ></textarea>
            </div>
            <button 
              type="submit" 
              className="w-full bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded"
            >
              Add Event
            </button>
          </form>
        </div>
        
        {/* Middle Column */}
        <div className="flex flex-col gap-4">
          {/* Upcoming Events */}
          <div className="bg-gray-800 rounded-lg shadow-lg p-4">
            <h2 className="text-xl font-semibold text-white mb-4">Upcoming Events</h2>
            {events
              .filter(event => new Date(event.start) >= new Date())
              .sort((a, b) => new Date(a.start) - new Date(b.start))
              .slice(0, 5)
              .map(event => (
                <div key={event.id} className="text-white mb-2">
                  <div className="font-semibold">{event.title}</div>
                  <div className="text-sm">{moment(event.start).format('MMM DD, YYYY')} - {event.venue}</div>
                  <div className="text-sm text-green-400">Income: ${event.income}</div>
                </div>
              ))}
            {events.filter(event => new Date(event.start) >= new Date()).length === 0 && (
              <p className="text-white">No upcoming events</p>
            )}
          </div>
          
          {/* Income Projection */}
          <div className="bg-gray-800 rounded-lg shadow-lg p-4">
            <h2 className="text-xl font-semibold text-white mb-2">Income Projection</h2>
            <p className="text-white mb-4">
              Total Projected Income (Next 30 Days): <strong>$3,000.00</strong>
            </p>
            <div className="h-48">
              <Line data={projectionData} options={{ maintainAspectRatio: false }} />
            </div>
          </div>
        </div>
        
        {/* Analytics Column extended */}
        <div className="bg-gray-800 rounded-lg shadow-lg p-4">
          <h2 className="text-xl font-semibold text-white mb-4">Quick Analytics</h2>
          <div className="grid grid-cols-2 gap-2 mb-4">
            <div className="bg-gray-700 p-3 rounded">
              <div className="text-sm text-gray-400">Events This Month</div>
              <div className="text-xl text-white font-semibold">7</div>
            </div>
            <div className="bg-gray-700 p-3 rounded">
              <div className="text-sm text-gray-400">Revenue This Month</div>
              <div className="text-xl text-white font-semibold">$5,200</div>
            </div>
            <div className="bg-gray-700 p-3 rounded">
              <div className="text-sm text-gray-400">Avg Event Value</div>
              <div className="text-xl text-white font-semibold">$743</div>
            </div>
            <div className="bg-gray-700 p-3 rounded">
              <div className="text-sm text-gray-400">Growth Rate</div>
              <div className="text-xl text-white font-semibold">+12%</div>
            </div>
          </div>
          <div className="bg-gray-700 p-3 rounded mb-4">
            <h3 className="text-lg font-semibold text-white mb-2">Category Distribution</h3>
            <div className="flex justify-between">
              <div>
                <div className="flex items-center">
                  <div className="w-3 h-3 bg-blue-500 mr-2"></div>
                  <span className="text-white">Wedding</span>
                </div>
                <div className="flex items-center">
                  <div className="w-3 h-3 bg-green-500 mr-2"></div>
                  <span className="text-white">Corporate</span>
                </div>
              </div>
              <div>
                <div className="flex items-center">
                  <div className="w-3 h-3 bg-purple-500 mr-2"></div>
                  <span className="text-white">Club</span>
                </div>
                <div className="flex items-center">
                  <div className="w-3 h-3 bg-yellow-500 mr-2"></div>
                  <span className="text-white">Private</span>
                </div>
              </div>
            </div>
          </div>
          <button 
            className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded mb-2"
            onClick={() => window.location.href = '/analytics'}
          >
            View Full Analytics
          </button>
          <button 
            className="w-full bg-purple-600 hover:bg-purple-700 text-white py-2 px-4 rounded"
            onClick={() => window.location.href = '/venues'}
          >
            Manage Venues
          </button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
